    private void opMultiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opMultiActionPerformed
        // TODO add your handling code here:
        opSimb.setText("*");
        int multn1 = Integer.parseInt(txtN1.getText());
        int multn2 = Integer.parseInt(txtN2.getText());
        int multRes = multn1 * multn2;
        
        res.setText(Integer.toString(multRes));
    }//GEN-LAST:event_opMultiActionPerformed

    private void opDivActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opDivActionPerformed
        // TODO add your handling code here:
        opSimb.setText("/");
        float divn1 = Float.parseFloat(txtN1.getText());
        float divn2 = Float.parseFloat(txtN2.getText());
        float divRes = divn1 / divn2;
        
        res.setText(Float.toString(divRes));
    }//GEN-LAST:event_opDivActionPerformed

    private void opRdivActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opRdivActionPerformed
        // TODO add your handling code here:
         opSimb.setText("%");
        float rdivn1 = Float.parseFloat(txtN1.getText());
        float rdivn2 = Float.parseFloat(txtN2.getText());
        float rdivRes = rdivn1 % rdivn2;
        
        res.setText(Float.toString(rdivRes));
    }//GEN-LAST:event_opRdivActionPerformed

    private void opSomaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opSomaActionPerformed
        // TODO add your handling code here:
        opSimb.setText("+");
        int sn1 = Integer.parseInt(txtN1.getText());
        int sn2 = Integer.parseInt(txtN2.getText());
        int sRes = sn1 + sn2;
        
        res.setText(Integer.toString(sRes));
        
        
        
    }//GEN-LAST:event_opSomaActionPerformed

