    private void opSubActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_opSubActionPerformed
        // TODO add your handling code here:
        opSimb.setText("-");
        int subn1 = Integer.parseInt(txtN1.getText());
        int subn2 = Integer.parseInt(txtN2.getText());
        int subRes = subn1 - subn2;
        
        res.setText(Integer.toString(subRes));
        
    }//GEN-LAST:event_opSubActionPerformed

